                    <input 
                      type="text" 
                      value={roleForm.endDate} 
                      onChange={(e) => setRoleForm({ ...roleForm, endDate: e.target.value })} 
                      placeholder="تاریخ پایان (1403/12/29) یا خالی بگذارید"
                      className="px-4 py-2 border-2 rounded-lg text-right"
