  // ==================== ORGANIZATION FORM ====================
  const OrganizationForm = () => {
    const [form, setForm] = useState(selectedOrg || {
      name: '', type: '', nationalId: '', country: 'ایران', city: '', address: '', phone: '', website: '', notes: ''
    });

    const submit = async () => {
      if (!form.name) {
        notify('نام سازمان الزامی است', 'error');
        return;
      }
      try {
        if (selectedOrg) {
