import React, { useEffect, useState } from 'react';
import {
  AlertCircle,
  BarChart3,
  Building2,
  Calendar,
  CheckCircle,
  ChevronLeft,
  DollarSign,
  Edit,
  FileText,
  Home,
  LogOut,
  Plus,
  Save,
  Search,
  Trash2,
  Users,
  X,
  Filter
} from 'lucide-react';
import { BarChart, Bar, PieChart, Pie, Cell, XAxis, YAxis, Tooltip, Legend, ResponsiveContainer } from 'recharts';

// API Configuration
const API_URL = 'http://localhost:3001/api';

const HaameeFinal = () => {
  const [currentScreen, setCurrentScreen] = useState('login');
  const [currentUser, setCurrentUser] = useState(null);
  const [loading, setLoading] = useState(false);
  const [notification, setNotification] = useState(null);

  const [persons, setPersons] = useState([]);
  const [organizations, setOrganizations] = useState([]);
  const [events, setEvents] = useState([]);
  const [applications, setApplications] = useState([]);
  const [payments, setPayments] = useState([]);

  const [selectedPerson, setSelectedPerson] = useState(null);
  const [selectedOrg, setSelectedOrg] = useState(null);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [selectedApp, setSelectedApp] = useState(null);
  const [selectedPayment, setSelectedPayment] = useState(null);

  const [searchText, setSearchText] = useState('');
  const [filterStatus, setFilterStatus] = useState('all');

  // API Helper
  const api = {
    get: async (endpoint) => {
      const response = await fetch(`${API_URL}${endpoint}`);
      if (!response.ok) throw new Error('Network error');
      return response.json();
    },
    post: async (endpoint, data) => {
      const response = await fetch(`${API_URL}${endpoint}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data)
      });
      if (!response.ok) throw new Error('Network error');
      return response.json();
    },
    put: async (endpoint, data) => {
      const response = await fetch(`${API_URL}${endpoint}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data)
      });
      if (!response.ok) throw new Error('Network error');
      return response.json();
    },
    delete: async (endpoint) => {
      const response = await fetch(`${API_URL}${endpoint}`, { method: 'DELETE' });
      if (!response.ok) throw new Error('Network error');
      return response.json();
    }
  };

  useEffect(() => {
    if (currentUser) loadAllData();
  }, [currentUser]);

  const loadAllData = async () => {
    try {
      const [p, o, e, a, pay] = await Promise.all([
        api.get('/persons'),
        api.get('/organizations'),
        api.get('/events'),
        api.get('/applications'),
        api.get('/payments')
      ]);
      setPersons(p);
      setOrganizations(o);
      setEvents(e);
      setApplications(a);
      setPayments(pay);
    } catch (error) {
      console.error('Load error:', error);
      notify('Ø®Ø·Ø§ Ø¯Ø± Ø¨Ø§Ø±Ú¯Ø°Ø§Ø±ÛŒ Ø¯Ø§Ø¯Ù‡â€ŒÙ‡Ø§', 'error');
    }
  };

  const notify = (message, type = 'success') => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), 3000);
  };

  const Notif = () => {
    if (!notification) return null;
    const ok = notification.type === 'success';
    return (
      <div className={`fixed top-4 left-1/2 transform -translate-x-1/2 z-50 px-6 py-4 rounded-lg shadow-2xl flex items-center gap-3 ${ok ? 'bg-green-500' : 'bg-red-500'} text-white`}>
        {ok ? <CheckCircle size={24} /> : <AlertCircle size={24} />}
        <span className="font-semibold">{notification.message}</span>
      </div>
    );
  };

